vertex:
#version 440 core

layout (location = 0) in vec3 aPos;
layout (location = 1) in vec3 aNormal;
layout (location = 2) in vec2 aTexCoords;
uniform mat4 m;
uniform mat4 vp;

out vec3 FragPos;
out vec3 Normal;
out vec2 TexCoords;

void main()
{
FragPos = vec3(m * vec4(aPos, 1.0));
Normal = normalize(mat3(transpose(inverse(m))) * aNormal);
TexCoords = aTexCoords;

gl_Position = vp * vec4(FragPos, 1.0);
}

fragment:
#version 440 core

in vec3 FragPos;
in vec3 Normal;
in vec2 TexCoords;
out vec4 FragColor;

uniform vec3 main_color = vec3(1.0,1.0,1.0);

void main()
{
FragColor = vec4(main_color, 1.0);
}
